import React from "react";
import { StyleSheet, Text, View, Image, TouchableOpacity } from "react-native";
import { Button } from "react-native-paper";
import ModalPassword from "./ModalPassword";
import * as SQLite from "expo-sqlite";

/**
 * The Logged class
 * @reactProps {object} navigation - Navigation props
 */
class Logged extends React.Component {
  /**
   * Pass the title option to the navigation
   */
  static navigationOptions = {
    title: "YouLog"
  };

  /**
   * visibility : The visibility of the update password button | 
   * password:  The user password |
   * canUpdatePassword: If the user can update the password or not
   * @type {{visible: boolean, password: string, id: string, canUpdatePassword: boolean}}
   */
  state = {
    visible: false,
    password: JSON.stringify(
      this.props.navigation.state.params.password
    ).replace(/\"/g, ""),
    id: JSON.stringify(this.props.navigation.state.params.id).replace(
      /\"/g,
      ""
    ),
    canUpdatePassword: true
  };

  /**
   * Update the password in the SQLite DB
   * @param {string} text - The new password
   */
  _updatePassword = text => {
    if (text !== this.state.password) {
      const usersDB = SQLite.openDatabase("users.db");
      usersDB.transaction(tx => {
        tx.executeSql(
          "CREATE TABLE IF NOT EXISTS users (id integer primary key not null, name text, email text, password text);"
        );
      });

      const query = "UPDATE users SET password = ? WHERE id = ?";
      const params = [text, this.state.id];

      usersDB.transaction(tx => {
        tx.executeSql(query, params);
      });
      this.setState({
        visible: false,
        canUpdatePassword: false
      });
    } else {
      this.setState({
        visible: false
      });
    }
  };

  /**
   * Hide the modal to change the password when user click on the cancel button
   */
  _cancel = () => {
    this.setState({
      visible: false
    });
  };

  /**
   * Show the modal to change the password
   */
  _showModal = () => {
    this.setState({
      visible: true
    });
  };

  /**
   * Convert the item from the sign in screen to json object
   * @param {string} params - The item to convert
   */
  _convertToJsObject = params => {
    return JSON.stringify(params).replace(/\"/g, "");
  };

  /**
   * Render the main screen
   * @returns {React.Component} - Logged Component
   */
  render() {
    const { visible, password, canUpdatePassword } = this.state;
    const { email, name } = this.props.navigation.state.params;
    const EMAIL = this._convertToJsObject(email);
    const NAME = this._convertToJsObject(name);

    return (
      <View style={styles.container}>
        <Image
          style={styles.congratulation}
          source={require("../media/congratulation.png")}
        />
        <Text style={styles.welcomeTitle}>Bienvenue {NAME} sur YouLog 🎉</Text>
        <Text style={styles.welcomeMail}>
          Vous êtes connecté sous {EMAIL} ✉️
        </Text>
        <Button
          mode={"contained"}
          labelStyle={{ color: "#4834d4" }}
          style={styles.btnSignUp}
          onPress={() => this.props.navigation.popToTop()}
        >
          Déconnexion
        </Button>
        {canUpdatePassword ? (
          <TouchableOpacity onPress={this._showModal}>
            <Text style={styles.password}>Changer votre mot de passe</Text>
          </TouchableOpacity>
        ) : null}
        <ModalPassword
          visible={visible}
          updatePassword={text => this._updatePassword(text)}
          cancel={this._cancel}
          password={password}
        />
      </View>
    );
  }
}

/**
 * Styles of the Logged Component
 */
const styles = StyleSheet.create({
  container: {
    flex: 1,
    flexDirection: "column",
    alignItems: "center",
    justifyContent: "center"
  },
  btnSignUp: {
    backgroundColor: "#fff",
    padding: 8,
    paddingLeft: 35,
    paddingRight: 35,
    marginLeft: 50,
    marginRight: 50
  },
  welcomeTitle: {
    textAlign: "center",
    marginTop: 20,
    marginBottom: 20,
    marginLeft: 30,
    marginRight: 30,
    fontSize: 20
  },
  welcomeMail: {
    textAlign: "center",
    marginTop: 20,
    marginBottom: 40,
    marginLeft: 30,
    marginRight: 30,
    fontSize: 14
  },
  congratulation: {
    width: 180,
    height: 180,
    marginBottom: 20
  },
  password: {
    marginTop: 20,
    color: "#4834d4",
    textDecorationLine: "underline"
  }
});

export default Logged;

import React from "react";
import { Modal, Text, View, StyleSheet } from "react-native";
import { TextInput, Button } from "react-native-paper";

/**
 * The ModalPassword class
 * @reactProps {string} password - User password
 * @reactProps {boolean} visible - The visibility of the update password button
 * @reactProps {function} hide - Hide the modal
 * @reactProps {function} cancel - Cancel the password update 
 * @reactProps {function} updatePassword - Update the user password 
 */
class ModalPassword extends React.Component {
  /**
   * ModalPassword constructor
   * @param {props} props - ModalPassword props
   */
  constructor(props) {
    super(props);
  }

  /**
   * password : The user password
   * @type {password: string}
   */
  state = {
    password: this.props.password
  };

  /**
   * Handle the change of password
   *  @param {string} text - The password to change
   */
  _setPassword = text => {
    this.setState({
      password: text
    });
  };

  /**
   * Render the password modal
   * @returns {React.Component} - Password Modal
   */
  render() {
    const { password } = this.state;
    return (
      <Modal
        animationType="slide"
        transparent={false}
        visible={this.props.visible}
        onRequestClose={() => {
          this.props.hide;
        }}
      >
        <View style={styles.container}>
          <Text style={styles.passwordTitle}>Modifier votre mot de passe</Text>
          <View style={styles.textInputContainer}>
            <TextInput
              onChangeText={value => {
                this._setPassword(value);
              }}
              autoCapitalize="none"
              style={styles.inputPassword}
              value={password}
            />
          </View>
          <Button
            mode={"contained"}
            labelStyle={{ color: "#fff" }}
            style={styles.btnCancel}
            onPress={() => this.props.cancel()}
          >
            Annuler
          </Button>
          <Button
            mode={"contained"}
            labelStyle={{ color: "#4834d4" }}
            style={styles.btnConfirm}
            onPress={() => this.props.updatePassword(password)}
          >
            Valider
          </Button>
        </View>
      </Modal>
    );
  }
}

/**
 * Styles of the ModalPassword Component
 */
const styles = StyleSheet.create({
  container: {
    flex: 1,
    flexDirection: "column",
    alignItems: "stretch",
    justifyContent: "center"
  },
  textInputContainer: {
    marginTop: 20
  },
  btnConfirm: {
    backgroundColor: "#fff",
    padding: 8,
    paddingLeft: 35,
    paddingRight: 35,
    marginLeft: 50,
    marginRight: 50,
    marginTop: 20
  },
  btnCancel: {
    backgroundColor: "#4834d4",
    padding: 8,
    paddingLeft: 35,
    paddingRight: 35,
    marginLeft: 50,
    marginRight: 50
  },
  inputPassword: {
    height: 50,
    marginLeft: 50,
    marginRight: 50,
    paddingLeft: 15,
    borderColor: "#ecf0f1",
    borderWidth: 1,
    borderRadius: 6,
    marginBottom: 25
  },
  passwordTitle: {
    textAlign: "center",
    fontSize: 20,
    marginBottom: 10
  }
});

export default ModalPassword;

import React from "react";
import { StyleSheet, View, Text, TextInput, Keyboard } from "react-native";
import { Button } from "react-native-paper";
import Toast from "react-native-root-toast";
import * as SQLite from "expo-sqlite";

/**
 * The SignIn class
 */
class SignIn extends React.Component {
  /**
   * Pass the title option to the navigation
   */
  static navigationOptions = {
    title: "Connexion"
  };

  /**
   * email: The user email |
   * password: The user password | 
   * users: Users list
   * @type {{email: string, password: string, users: array}}
   */
  state = {
    email: "",
    password: "",
    users: []
  };

  /**
   * Call the method selectUser to get all the users of the DB
   */
  componentDidMount() {
    this._selectUsers();
  }

  /**
   * Select all the users from the SQLite DB
   */
  _selectUsers = () => {
    const usersDB = SQLite.openDatabase("users.db");
    usersDB.transaction(tx => {
      tx.executeSql(
        "CREATE TABLE IF NOT EXISTS users (id integer primary key not null, name text, email text, password text);"
      );
    });

    const query = "SELECT * FROM users";
    const params = [];
    usersDB.transaction(tx => {
      tx.executeSql(query, params, (_, { rows }) => {
        if (rows.length > 0) {
          this.setState({
            users: rows._array
          });
        }
      });
    });
  };

  /**
   * Handle the change of email
   * @param {string} text - The email to change
   */
  _onChangeEmail = text => {
    this.setState({
      email: text
    });
  };

  /**
   * Handle the change of password
   * @param {string} text - The password to change
   */
  _onChangePassword = text => {
    this.setState({
      password: text
    });
  };

  /**
   * Check if the informations put by the user are equivalent to a user from the SQLite DB
   * @param {} email - The email put by the user
   * @param {} password - The password put by the user
   */
  _checkUser = (email, password) => {
    const { users } = this.state;
    for (let i = 0; i < users.length; i++) {
      const element = users[i];
      if (email === element.email && password === element.password) {
        this.props.navigation.push("Logged", {
          name: element.name,
          email: element.email,
          password: element.password,
          id: element.id
        });
        break;
      } else {
        Toast.show("Le mail ou le mot de passe est incorrect", {
          duration: Toast.durations.LONG,
          position: Toast.positions.TOP + 60,
          shadow: false,
          opacity: 1
        });
      }
    }
  };

  /**
   * Check if the email and password put by the user are correct
   * @param {} email - The email put by the user
   * @param {} password - The password put by the user
   */
  _checkTextInput = (email, password) => {
    Keyboard.dismiss();
    let reg = /^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/;

    if (reg.test(email) === false || !password.length > 0) {
      Toast.show("Les informations saisies sont incorrectes", {
        duration: Toast.durations.LONG,
        position: Toast.positions.TOP + 60,
        shadow: false,
        opacity: 1
      });
    } else {
      this._checkUser(email, password);
    }
  };

  /**
   * Render the sign in screen
   * @returns {React.Component} - SignIn Component
   */
  render() {
    const { email, password } = this.state;

    return (
      <View style={styles.container}>
        <Text style={styles.signInTitle}>Connectez-vous</Text>
        <View style={styles.textInputContainer}>
          <TextInput
            style={styles.input}
            placeholder="E-mail"
            textContentType="emailAddress"
            keyboardType="email-address"
            onChangeText={this._onChangeEmail}
            autoCapitalize="none"
            returnKeyType={"next"}
            onSubmitEditing={() => {
              this.passwordTextInput.focus();
            }}
            blurOnSubmit={false}
          />
          <TextInput
            ref={input => {
              this.passwordTextInput = input;
            }}
            style={styles.input}
            placeholder="Mot de passe"
            textContentType="password"
            autoCapitalize="none"
            secureTextEntry={true}
            onChangeText={this._onChangePassword}
          />
        </View>
        <Button
          mode={"contained"}
          labelStyle={{ color: "#fff" }}
          style={styles.btnSignIn}
          onPress={() => {
            this._checkTextInput(email.trim(), password);
          }}
        >
          Connexion
        </Button>
      </View>
    );
  }
}

/**
 * Styles of the SignIn Component
 */
const styles = StyleSheet.create({
  container: {
    flex: 1,
    flexDirection: "column",
    alignItems: "stretch",
    justifyContent: "center"
  },
  textInputContainer: {
    marginTop: 20
  },
  input: {
    height: 50,
    marginLeft: 50,
    marginRight: 50,
    paddingLeft: 15,
    borderColor: "#ecf0f1",
    borderWidth: 1,
    borderRadius: 6,
    marginBottom: 25
  },
  btnSignIn: {
    backgroundColor: "#4834d4",
    padding: 8,
    paddingLeft: 35,
    paddingRight: 35,
    marginLeft: 50,
    marginRight: 50,
    marginBottom: 40
  },
  signInTitle: {
    textAlign: "center",
    fontSize: 20,
    marginBottom: 10
  }
});

export default SignIn;

import React from "react";
import {
  StyleSheet,
  Text,
  TextInput,
  View,
  Keyboard,
  ScrollView
} from "react-native";
import { Button } from "react-native-paper";
import Toast from "react-native-root-toast";
import * as SQLite from "expo-sqlite";

/**
 * The SignUp class
 */
class SignUp extends React.Component {
  /**
   * Pass the title option to the navigation
   */
  static navigationOptions = {
    title: "Inscription"
  };

  /**
   * name: The user name | 
   * email: The user email |
   * password: The user password
   * @type {{name: string, email: string, password: string}}
   */
  state = {
    name: "",
    email: "",
    password: ""
  };

  /**
   * Handle the change of name
   *  @param {string} text - The name to change
   */
  _onChangeName = text => {
    this.setState({
      name: text
    });
  };

  /**
   * Handle the change of email
   *  @param {string} text - The email to change
   */
  _onChangeEmail = text => {
    this.setState({
      email: text
    });
  };

  /**
   * Handle the change of password
   *  @param {string} text - The password to change
   */
  _onChangePassword = text => {
    this.setState({
      password: text
    });
  };

  /**
   * Insert the user in the SQLite DB
   * @param  {string} name - The name of the user
   * @param  {string} email - The email of the user
   * @param  {string} password - The password of the user
   */
  _insert = (name, email, password) => {
    const usersDB = SQLite.openDatabase("users.db");
    usersDB.transaction(tx => {
      tx.executeSql(
        "CREATE TABLE IF NOT EXISTS users (id integer primary key not null, name text, email text, password text);"
      );
    });

    const query = "INSERT INTO users(name, email, password) VALUES (?,?,?);";
    const usr = [name, email, password];

    usersDB.transaction(tx => {
      tx.executeSql(query, usr);
    });
  };

  /**
   * Check the value in the text input
   * @param  {string} email - Email of the user
   * @param  {string} password - Password of the user
   * @param  {string} name - Name of the user
   */
  _checkTextInput = (email, password, name) => {
    Keyboard.dismiss();
    let reg = /^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/;

    if (
      reg.test(email) === false &&
      !email.length > 0 &&
      !password.length > 0
    ) {
      Toast.show("Entrez un email valide", {
        duration: Toast.durations.LONG,
        position: Toast.positions.TOP + 60,
        shadow: false,
        opacity: 1
      });
    } else {
      this._insert(name, email, password);
      this.props.navigation.navigate("SignIn");
    }
  };

  /**
   * Render the sign up screen
   * @returns {React.Component} - SignUp Component
   */
  render() {
    const { name, email, password } = this.state;
    return (
      <View style={styles.container}>
        <Text style={styles.signUpTitle}>Créez votre compte</Text>
        <View style={styles.textInputContainer}>
          <ScrollView>
            <TextInput
              style={styles.input}
              placeholder="Nom"
              onChangeText={this._onChangeName}
              autoCapitalize="none"
              returnKeyType={"next"}
              onSubmitEditing={() => {
                this.emailTextInput.focus();
              }}
              blurOnSubmit={false}
            />
            <TextInput
              ref={input => {
                this.emailTextInput = input;
              }}
              style={styles.input}
              placeholder="E-mail"
              textContentType="emailAddress"
              keyboardType="email-address"
              onChangeText={this._onChangeEmail}
              autoCapitalize="none"
              returnKeyType={"next"}
              onSubmitEditing={() => {
                this.passwordTextInput.focus();
              }}
              blurOnSubmit={false}
            />
            <TextInput
              ref={input => {
                this.passwordTextInput = input;
              }}
              style={styles.input}
              placeholder="Mot de passe"
              textContentType="password"
              secureTextEntry={true}
              onChangeText={this._onChangePassword}
            />
          </ScrollView>
        </View>
        <Button
          mode={"contained"}
          labelStyle={{ color: "#fff" }}
          style={styles.btnSignIn}
          onPress={() =>
            this._checkTextInput(email.trim(), password, name.trim())
          }
        >
          Valider
        </Button>
      </View>
    );
  }
}

/**
 * Styles of the SignUp Component
 */
const styles = StyleSheet.create({
  container: {
    flex: 1,
    flexDirection: "column",
    alignItems: "stretch",
